
public class Test06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result;
		double resultDouble;
		
		result = 3/2;
		resultDouble = 3/2;
		
		System.out.println(result);
		System.out.println(resultDouble);
		
		resultDouble = 3.0/2.0;
		System.out.println(resultDouble);
		
		resultDouble = (double)3/(double)2;
		System.out.println(resultDouble);
	}

}
